style:
.keyboard {
  margin-top: .6rem;
  margin-bottom: .6rem;
}

.row:nth-child(-n+3) .key:nth-child(6) {
   margin-right: 10rem;
}

.row:nth-child(-n+3) .key:nth-child(3),
.row:nth-child(-n+3) .key:nth-child(10),
.row:nth-child(4) .key:nth-child(3),
.row:nth-child(4) .key:nth-child(12),
.row:nth-child(-n+3) .key:nth-child(6),
.row:nth-child(-n+3) .key:nth-child(7),
.row:nth-child(4) .key:nth-child(6),
.row:nth-child(4) .key:nth-child(9) {
   transform: translateY(-.3rem);
}

.row:nth-child(-n+3) .key:nth-child(4),
.row:nth-child(-n+3) .key:nth-child(9),
.row:nth-child(4) .key:nth-child(4),
.row:nth-child(4) .key:nth-child(11) {
   transform: translateY(-.8rem);
}

.row:nth-child(-n+3) .key:nth-child(5),
.row:nth-child(-n+3) .key:nth-child(8),
.row:nth-child(4) .key:nth-child(5),
.row:nth-child(4) .key:nth-child(10) {
   transform: translateY(-.6rem);
}

.row:nth-child(4) .key:nth-child(7) {
  transform: rotate(15deg) translateY(.5rem);
}

.row:nth-child(4) .key:nth-child(8) {
  transform: rotate(-15deg) translateY(.5rem);
}
.row:nth-child(5) {
  justify-content: center;
}
.row:nth-child(5) .key:nth-child(1),
.row:nth-child(5) .key:nth-child(6) {
  transform: translateY(-.3rem);
}
.row:nth-child(5) .key:nth-child(3) {
  margin-right: 1.75rem;
  margin-left: .5rem;
  transform: rotate(15deg) translateY(.25rem);
}
.row:nth-child(5) .key:nth-child(4) {
  margin-right: .5rem;
  transform: rotate(-15deg) translateY(.25rem);
}
